/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import { useState, useEffect } from "react";

function Profile() {
  // State
  const [profile, setProfile] = useState("");

  async function getProfile() {
    // Get user.
    const cookie = document.cookie;
    let user_id;
    let data;

    if (cookie !== "") {
      user_id = cookie
        .split(";")
        .find((e) => e.startsWith("user_id="))
        .split("=")[1];
      data = await lg_api("accounts/profile-router/profile-router/", {
        params: {
          user_id: user_id,
        },
      });
    }
    setProfile({ data });
  }

  return (
    <main style="margin-top: 5em;">
      <div class="container mb-3" style="margin-top: 1em;">
        <div class="row">
          <div class="col-sm-4 d-flex justify-content-center">
            <img
              src={profile.picture}
              alt={profile.user.username}
              class="rounded-circle"
              width="170px"
              height="170px"
              style="object-fit: cover;"
            />
            {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                            /> */}
          </div>

          <div class="col-sm-8">
            <h2 style="font-weight: 100;">
              {profile.user.username}
              <a
                href="{% url 'users:profile-update' request.user profile.id %}"
                class="ml-5 btn btn-sm btn-outline-info"
              >
                Edit profile
              </a>
              {/*
                                <a
                                    href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                </a>
                            */}
            </h2>

            <div class="row mt-2" style="font-size: 1.2em">
              <div class="col-sm-4">
                {profile.posts_count}
                <b>785</b> posts
              </div>
              <div class="col-sm-4">
                {profile.followers}
                <b>1,401</b> followers
              </div>
              <div class="col-sm-4">
                {profile.following}
                <b>491</b> following
              </div>
            </div>
            <div class="row mt-4">
              <div class="col-sm-12">
                <p>{profile.biography}</p>
              </div>
              <div class="col-sm-12">
                <p>Website: {profile.website}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <hr />

      <div class="container" id="user-posts">
        <div class="row mt-3">
          {/* {% for post in posts %} */}
          <div class="col-sm-4 pt-5 pb-5 pr-5 pl-5 d-flex justify-content-center align-items-center">
            <a href="" class="border">
              <img
                src="{{ post.photo.url }}"
                alt="{{ post.title }}"
                class="img-fluid"
              />
            </a>
          </div>
        </div>
      </div>
    </main>
  );
}

export { Profile };
