/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Profile.css";
import React from "react";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = { profile: [] };
  }

  componentDidMount() {
    try {
      async function fch() {
        let auth_user = window.localStorage.getItem("user");
        console.log(auth_user);
        if (auth_user !== null) {
          await lg_api(`accounts/profile/${auth_user}`).then((res) => {
            this.setState({ profile: res.data });
            console.log("s", this.state.profile);
          });
        }
      }
      fch();
    } catch (err) {
      console.log(err);
      this.setState({ profile: err });
    }
  }

  render() {
    let current_user = this.state.profile;
    return (
      <main>
        <React.Fragment>
          <div className="container mb-3">
            <div className="row">
              <div className="col-sm-4 d-flex justify-content-center">
                <img
                  src={current_user.picture}
                  alt={current_user.user}
                  className="rounded-circle picture-profile"
                  width="170px"
                  height="170px"
                />
                {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                            /> */}
              </div>

              <div className="col-sm-8">
                <h2 className="username-h2">
                  {current_user.user.username}
                  <span
                    className="ml-5 btn btn-sm btn-outline-info"
                    onClick={() => (window.location.hash = "#edit-profile")}
                  >
                    Edit profile
                  </span>
                  {/*
                                <a
                                href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                </a>
                            */}
                </h2>

                <div className="row mt-2 info-profile">
                  <div className="col-sm-4">
                    <b>{current_user.posts_count}</b> posts
                  </div>
                  <div className="col-sm-4">
                    <b>{current_user.followers}</b> followers
                  </div>
                  <div className="col-sm-4">
                    <b>{current_user.following}</b> following
                  </div>
                </div>
                <div className="row mt-4">
                  <div className="col-sm-12">
                    <p>{current_user.biography}</p>
                  </div>
                  <div className="col-sm-12">
                    <p>Website: {current_user.website}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <hr />
          {/* {posts} */}
        </React.Fragment>
      </main>
    );
  }
}

