/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Login.css";
import React from "react";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = { log: [] };
    this.username = props.username;
    this.password = props.password;
    this.img = 'http://localhost:8000/media/logo_instagram.png'
  }

  componentDidMount() {
    lg_api
      .post("accounts/login/", {
        username: "root",
        password: "admin123",
      })
      .then((res) => {
        this.setState({ log: res.data });
      });
  }

  render() {


    return(

      <section class="login">
		<div class="login_box">
			<div class="left">
				<div class="top_link"><a href><img src="https://drive.google.com/u/0/uc?id=16U__U5dJdaTfNGobB_OpwAJ73vM50rPV&export=download" alt=""/>Return home</a></div>
				<div class="contact">
					<form action="" />
						<h3>SIGN IN</h3>
						<input type="text" placeholder="USERNAME"/>
						<input type="text" placeholder="PASSWORD"/>
						<button class="submit">LET'S GO</button>
				</div>
			</div>
			<div class="right">
				<div class="right-text">
					<h2>LOGO</h2>
					<h5>A great company to work with!</h5>
				</div>
				<div class="right-inductor">
          <img src="" alt=""/>

          </ div>
			</div>
		</div>
	  </section>
    )
  }
}

export { Login };




