/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Login.css";
import React from "react";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = { log: [] };
    this.username = props.username;
    this.password = props.password;
    this.img = "http://localhost:8000/media/logo_instagram.png";
  }

  componentDidMount() {
    lg_api
      .post("accounts/login/", {
        username: "root",
        password: "admin123",
      })
      .then((res) => {
        this.setState({ log: res.data });
      });
  }

  render() {
    return (
      <section>
        <div className="login">
          <div className="login_box">
            <div className="left">
              <div className="top_link">
                <span href>
                  <img
                    src="https://drive.google.com/u/0/uc?id=16U__U5dJdaTfNGobB_OpwAJ73vM50rPV&export=download"
                    alt=""
                  />
                  Return home
                </span>
              </div>
              <div className="contact">
                <form action="" />
                <h3 className="sign-in">SIGN IN</h3>
                <input type="text" placeholder="USERNAME" />
                <input type="text" placeholder="PASSWORD" />
                <button className="submit">LET'S GO</button>
              </div>
            </div>
            <div className="right">
              <div className="right-text">
                <h2>LOGO</h2>
                <h5>A great company to work with!</h5>
              </div>
              <div className="right-inductor">
                <img src="" alt="" />
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export { Login };

