/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import { useState} from "react";

function Profile() {
  // State
  const [profile, setProfile] = useState("");
  const [own_posts, setOwnPosts] = useState([]);

  async function getProfile() {
    // Get user.
    const cookie = document.cookie;
    let user_id;
    let data;

    if (cookie !== "") {
      user_id = cookie
        .split(";")
        .find((e) => e.startsWith("user_id="))
        .split("=")[1];
      data = await lg_api("accounts/profile-router/profile-router/", {
        params: {
          user_id: user_id,
        },
      });
    }
    console.log(data.data[0].user.username)
    setProfile(data.data);
  }

  async function ownPosts() {
    await lg_api("posts/")
      .then((res) => {
        setOwnPosts(res.data);
      })
      .catch((res) => {
        console.log(res.data);
      });
  }

  getProfile();
  ownPosts();

  let posts = own_posts.map((e) => {
    return (
      <div className="container" id="user-posts">
        <div class="row mt-3">
          <div className="col-sm-4 pt-5 pb-5 pr-5 pl-5 d-flex justify-content-center align-items-center">
            <a href className="border">
              <img
                src={own_posts.post_image}
                alt={own_posts.title}
                className="img-fluid"
              />
            </a>
          </div>
        </div>
      </div>
    );
  });

  return (
    <main>
      <div className="container mb-3">
        <div className="row">
          <div className="col-sm-4 d-flex justify-content-center">
            <img
              src={profile.picture}
              alt={profile.user.username}
              className="rounded-circle picture-profile"
              width="170px"
              height="170px"
            />
            {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                            /> */}
          </div>

          <div className="col-sm-8">
            <h2 className="username-h2">
              {profile.user.username}
              <span
                className="ml-5 btn btn-sm btn-outline-info"
                onClick={() => (window.location.hash = "#edit-profile")}
              >
                Edit profile
              </span>
              {/*
                                <a
                                href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                </a>
                            */}
            </h2>

            <div className="row mt-2 info-profile">
              <div className="col-sm-4">
                {profile.posts_count}
                <b>785</b> posts
              </div>
              <div className="col-sm-4">
                {profile.followers}
                <b>1,401</b> followers
              </div>
              <div className="col-sm-4">
                {profile.following}
                <b>491</b> following
              </div>
            </div>
            <div className="row mt-4">
              <div className="col-sm-12">
                <p>{profile.biography}</p>
              </div>
              <div className="col-sm-12">
                <p>Website: {profile.website}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <hr />

      {posts}
    </main>
  );
}

export { Profile };
