import { lg_api } from "../../__modules__";

async function likePost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("likes_post")) {
    let url = `posts/${e.nativeEvent.path[4].id}/`;
    console.log(e);
    let token = window.localStorage.getItem("token");
    let profile_likedit = window.localStorage.getItem("profile_auth");

    await lg_api(url, {
      method: "put",
      headers: {
        Authorization: `Token ${token}`,
      },
      data: {
        profile_likedit: profile_likedit,
      },
    })
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  }
}

