/* List posts from lazygram with react */

import { lg_api } from "../../__modules__";
import "../../css/posts/ListPosts.css";
import React from "react";
import {
  BsFillHeartFill,
  BsFillChatFill,
  BsFillBookmarkFill,
  BsShareFill,
} from "react-icons/bs";

import {
  likePost,
  inputComment,
  allCommentsPost,
} from "../../utils/posts/posts";

class ListPosts extends React.Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { posts: [] };
    this.token = window.localStorage.getItem("token");
    this.http_media = "http://localhost:8000/media/";
    this.post_img = "";
    this.handledPosts = (e) => {
      likePost(e);
    };
    this.getLikes = this.getLikes.bind(this);
    this.addHTTPMedia = this.addHTTPMedia.bind(this);
    this.showComments = this.showComments.bind(this);
    this.goToProfile = this.goToProfile.bind(this);
    this.allCommentsPost = this.allCommentsPost.bind(this);
    this.commentComponent = this.commentComponent.bind(this);
  }

  commentComponent(data) {
    let comment = (
      <div>
        <img src={data.commented_by.picture} alt="s" />
      </div>
    );
    let divComment = document.createElement("div");
    let profileName = document.createElement("h6");
    let profilePic = document.createElement("img");
    let pComment = document.createElement("span");
    let containerBig = document.createElement("div");
    let commentValue;
    // Getting parent node
    let container = document.querySelector(`#id_show_comments_${data[0].post}`);
    let allDivs = [];
    divComment.classList = "row pt-2";
    profilePic.classList =
      "profile-img-post rounded-circle go-to-profile col-sm-3";
    profileName.classList = "h6 col-sm-3";
    pComment.classList = "fs-6";

    data.map((value) => {
      profileName.innerHTML = value?.commented_by?.user?.username;
      profilePic.src = value?.commented_by?.picture;
      profilePic.alt = value?.commented_by?.user?.username;
      pComment.innerHTML = value?.comment;

      divComment.append(profilePic, profileName, pComment);
      containerBig.appendChild(divComment);
      return 0;
    });
    console.log(containerBig);
    container.appendChild(divComment);
  }

  /**
   * If a profile picture not find,
   * return a profile picture by default.
   * @param {*} post
   */
  goToProfile(e) {
    console.log(e.nativeEvent.path[2].childNodes[1].innerText);
    let profile =
      e.nativeEvent.path[2].childNodes[1].innerText || e.target.innerText;
    window.localStorage.setItem("profile", `${profile}`);
    window.location.hash = "#profile";
  }

  allCommentsPost(id_post) {
    let token = window.localStorage.getItem("token");

    console.log(id_post);
    lg_api(`comments/`, {
      headers: {
        Authorization: `Token ${token}`,
      },
      params: { id_post: id_post },
    })
      .then((res) => {})
      .catch((err) => {
        console.error(err.response);
      });
  }

  showComments(e) {
    let len = e.target.id.split("").length;
    let id = e.target.id.slice(11, len);
    allCommentsPost(id);
  }

  addHTTPMedia(post) {
    // If post image not contains http_media, add.
    if (!post.picture.startsWith(this.http_media)) {
      this.post_img = this.http_media + post.picture;
    } else {
      this.post_img = post.picture;
    }
  }

  async getLikes(e) {
    await lg_api(
      `posts/${String(e.nativeEvent.path[1].id).startsWith("likes_post")}`,
      {
        headers: {
          Authorization: `Token ${this.token}`,
        },
      }
    ).then((res) => {
      console.log(e);
    });
  }

  componentDidMount() {
    // Getting posts.
    lg_api("posts/", {
      headers: {
        Authorization: `Token ${this.token}`,
      },
    })
      .then((res) => {
        this.setState({ posts: res.data });
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  }

  render() {
    return (
      <section className="">
        {this.state.posts.map((post, i) => {
          this.addHTTPMedia(post);

          return (
            <div
              key={i}
              id={post.id}
              className="row w-50 mx-auto mb-4 posts"
              onClick={(e) => this.handledPosts(e)}
            >
              {/* User profile image */}
              <div className="row align-items-center">
                <div className="col-sm-1">
                  <img
                    className="profile-img-post rounded-circle go-to-profile"
                    src={post.profile?.picture}
                    alt={post.profile?.picture}
                    onClick={(e) => this.goToProfile(e)}
                  />
                </div>

                {/* User profile name */}
                <div className="col-sm">
                  <h5
                    className="h5 go-to-profile"
                    onClick={(e) => this.goToProfile(e)}
                  >
                    {post.profile?.user?.username}
                  </h5>
                </div>

                {/* Created */}
                <div className="">
                  <p className="h6 pt-1 px-1">Created: {post.created}</p>
                </div>
              </div>

              {/* Post img */}
              <div className="">
                <img
                  className="w-100 rounded-top img-post"
                  src={this.post_img}
                  alt={this.post_img}
                />

                {/* Like | comment | share | save post */}
                <div className="feedback">
                  {/* Like */}
                  <BsFillHeartFill
                    className="icon-size-post"
                    id={`id_like_post_${post.id}`}
                  />

                  {/* Comment */}
                  <BsFillChatFill
                    className="icon-size-post"
                    onClick={(e) => {
                      let a = inputComment(e.target.parentElement.id);
                      e.nativeEvent.path[4].appendChild(a);
                    }}
                    id={`id_comments_post_${post.id}`}
                  />

                  {/* Share */}
                  <BsShareFill
                    className="icon-size-post"
                    id={`id_share_post_${post.id}`}
                  />

                  {/* Save post */}
                  <BsFillBookmarkFill
                    className="icon-size-post"
                    id={`id_save_post_${post.id}`}
                  />
                </div>

                {/* Likes count */}
                <div>
                  <span
                    className="text-end fs-6 likes"
                    id={`id_show_likes_${post.id}`}
                  >
                    {post?.likes} Likes
                  </span>
                </div>

                {/* Description */}
                <div className="mt-2">
                  <p className="fs-6">
                    <span className="h6 me-2">{post.description}</span>
                  </p>
                </div>

                {/* Comments */}
                <div>
                  <button
                    className="btn btn-outline-info"
                    id={`id_btn_cmt_${post.id}`}
                    onClick={(e) => {
                      this.showComments(e);
                    }}
                  >
                    Show comments...
                  </button>
                  <div className="" id={`id_show_comments_${post.id}`}>
                    {this.state?.comments}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </section>
    );
  }
}

export { ListPosts };
