import React from "react";
import "../../css/Main.css";
import { HeadMenu, ListPosts, Profile, Login } from "../../__modules__";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      hash: "",
    };
    this.actual_hash = window.location.hash;
  }

  componentDidMount() {
    window.onbeforeunload = window.location.hash;
    window.addEventListener("hashchange", () => {
      this.setState({ hash: window.location.hash });
    });
  }

  render() {
    if (
      this.state.hash.startsWith("#profile") ||
      this.actual_hash.startsWith("#profile")
    ) {
      return (
        <React.Fragment>
          <HeadMenu />
          <ListPosts />
        </React.Fragment>
      );
    }

    if (
      this.state.hash.startsWith("#edit-profile") ||
      this.actual_hash.startsWith("#edit-profile")
    ) {
      return (
        <React.Fragment>
          <HeadMenu />
          <ListPosts />
        </React.Fragment>
      );
    }

    if (this.state.hash.startsWith("") || this.actual_hash.startsWith("")) {
      console.log("Yeh");
      return (
        <React.Fragment>
          <HeadMenu />
          <ListPosts />
        </React.Fragment>
      );
    }
  }
}
export { App };
