/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import { useState, useEffect } from "react";

function Profile() {
  // State
  const [profile, setProfile] = useState("");

  async function getProfile() {
    // Get user.
    const cookie = document.cookie;
    let user_id;
    let data;

    if (cookie !== "") {
      user_id = cookie
        .split(";")
        .find((e) => e.startsWith("user_id="))
        .split("=")[1];
      data = await lg_api("accounts/profile-router/profile-router/", {
        params: {
          user_id: user_id,
        },
      });
    }

    setProfile({data})
  }


}

export { Profile };
