import React from "react";
import "../../css/Main.css";
import {
  HeadMenu,
  ListPosts,
  Profile,
  Login,
  EditProfile,
  CreateUser,
  VerifyToken,
} from "../../__modules__";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      hash: "",
    };
    this.actual_hash = window.location.hash;
  }

  componentDidMount() {
    window.addEventListener("hashchange", () => {
      this.setState({ hash: window.location.hash });
    });
  }

  render() {
    // Profile component.
    if (
      this.state.hash.startsWith("#profile") ||
      this.actual_hash.startsWith("#profile")
    ) {
      return (
        <React.Fragment>
          <HeadMenu />
          <Profile />
        </React.Fragment>
      );
    }

    // Edit profile component.
    if (
      this.state.hash.startsWith("#edit-profile") ||
      this.actual_hash.startsWith("#edit-profile")
    ) {
      return (
        <React.Fragment>
          <HeadMenu />
          <EditProfile />
        </React.Fragment>
      );
    }

    // Register component
    if (
      this.state.hash.startsWith("#register") ||
      this.actual_hash.startsWith("#register")
    ) {
      return (
        <React.Fragment>
          <CreateUser />
        </React.Fragment>
      );
    }

    // Verify token component.
    if (
      this.state.hash.startsWith("#verify") ||
      this.actual_hash.startsWith("#verify")
    ) {
      return (
        <React.Fragment>
          <VerifyToken />
        </React.Fragment>
      );
    }

    // New post component
    if (
      this.state.hash.startsWith("#newpost") ||
      this.actual_hash.startsWith("#newpost")
    ) {
      return (
        <React.Fragment>
          <CreatePost />
        </React.Fragment>
      );
    }

    // Logout component.
    if (
      this.state.hash.startsWith("#logout") ||
      this.actual_hash.startsWith("#logout") ||
      this.state.hash.startsWith("#login") ||
      this.actual_hash.startsWith("#login")
    ) {
      return (
        <React.Fragment>
          <Login />
        </React.Fragment>
      );
    } else {
      return (
        <React.Fragment>
          <HeadMenu />
          <ListPosts />
        </React.Fragment>
      );
    }
  }
}
export { App };
