/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import React from "react";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = { profile: [] };
  }

  componentDidMount() {
    try {
      console.log(document.cookie);
      if (document.cookie !== "") {
        let user_id_ = document.cookie
          .split(";")
          .find((e) => e.startsWith("user_id="))
          .split("=")[1];

        lg_api("accounts/profile-router/profile-router/", {
          params: {
            user_id: user_id_,
          },
        }).then((res) => {
          this.setState({ profile: res.data[0] });
          console.log(this.profile);
        });
      }
    } catch (err) {
      console.log(err);
    }
  }

  render() {
    return (
      <main>

      </main>
    );
  }
}

