import React from "react";
import { lg_api } from "../../__modules__";

class CreatePost extends React.Component {
  constructor() {
    super();
    this.state = { post: [] };
  }

  componentDidMount() {
    let form_post = new FormData();
    let post_image = document.querySelector("#id_post_image").files[0];
    let description = document.querySelector("#id_description").value;

    if (post_image !== null) {
      form_post.append("post_image", post_image);
    }

		// Description
    if (description !== null) {
      form_post.append("description", description);
    }

    lg_api("posts/", {
      method: "post",
      headers: {
        Authorization: `Token ${this.token}`,
      },
      data: form_post,
    });
  }
}

export { CreatePost };
