// Verify token.

import { lg_api } from "../../__modules__";
import React from "react";
import { BsFillExclamationSquareFill } from "react-icons/bs";

class VerifyToken extends React.Component {
  constructor() {
    super();
    this.state = { token: "" };
  }

  componentDidMount() {
    let token_data = new FormData();
    let new_token = document.querySelector("#id_token");
    token_data.append("token", new_token);

    lg_api("accounts/users/verify-account/", {
      method: "post",
      data: {
        token: token_data,
      },
    }).then((res) => {
      window.localStorage.setItem("token");
    });
  }

  render() {
    return (
      <div className="w-25 mx-auto">
        <h2 className="h2">Input token</h2>
        <form method="post" className="row">
          <BsFillExclamationSquareFill className="fs-2" />{" "}
          <input
            type="text"
            className="form-control "
            id="id_token"
            required
            placeholder="Input your token"
          />
        </form>
      </div>
    );
  }
}

export { VerifyToken };
