/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Profile.css";
import React from "react";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = {
      profile: [],
      followers: [],
      following: [],
      editOrFollowState: "Edit profile",
      btnClassEditOrFollow: "ml-5 btn btn-sm btn-outline-info",
    };
    this.editorfollow = this.editorfollow.bind(this);
  }

  editorfollow(e) {
    let user = window.localStorage.getItem("user");
    if (user !== window.location.hash) {
      this.setState({editOrFollowState:"ml-5 btn btn-sm btn-primary"})
    }
  }
  componentDidMount() {
    try {
      let current_profile = window.localStorage.getItem("current_profile");

      // Get user
      if (current_profile !== null) {
        lg_api(`accounts/profile/${current_profile}`, {
          headers: {
            Authorization: `Token ${window.localStorage.getItem("token")}`,
          },
        }).then((res) => {
          this.setState({ profile: [res.data] });
          console.log(res.data);
        });

        // Get followers
        lg_api(`accounts/followers/${current_profile}`, {
          headers: {
            Authorization: `Token ${window.localStorage.getItem("token")}`,
          },
        }).then((res) => {
          this.setState({ followers: [res.data] });
        });

        // Get following
        lg_api(`accounts/following/${current_profile}`, {
          headers: {
            Authorization: `Token ${window.localStorage.getItem("token")}`,
          },
        }).then((res) => {
          this.setState({ following: [res.data] });
          console.log(this.state.followers);
        });
      }
    } catch (err) {
      console.log(err);
      this.setState({ profile: [err] });
    }
  }

  render() {
    return (
      <main>
        {this.state.profile.map((value, i) => {
          if (value.picture === undefined) {
            value.picture = "http://localhost:8000/media/user_circle.svg";
          }
          return (
            <React.Fragment key={i}>
              <div className="container mb-3">
                <div className="row">
                  <div className="col-sm-4 d-flex justify-content-center">
                    <img
                      src={value.picture}
                      alt={value.user.username}
                      className="rounded-circle picture-profile"
                      width="170px"
                      height="170px"
                    />
                  </div>

                  <div className="col-sm-8">
                    <h2 className="username-h2">
                      {value.user.username}
                      <span
                        className={this.state?.btnClassEditOrFollow}
                        onClick={(e) => this.editorfollow(e)}
                      >
                        {this.state?.editOrFollowState}
                      </span>

                      {/* <a href className="ml-5 btn btn-sm btn-primary">
                        Follow
                      </a> */}
                    </h2>

                    <div className="row mt-2 info-profile">
                      <div className="col-sm-4">
                        <b>{value.posts_count}</b> posts
                      </div>
                      <div className="col-sm-4">
                        <b>{this.state.followers[0]?.followers_length}</b>{" "}
                        followers
                      </div>
                      <div className="col-sm-4">
                        <b>{this.state.following[0]?.following_length}</b>{" "}
                        following
                      </div>
                    </div>
                    <div className="row mt-4">
                      <div className="col-sm-12">
                        <p>{value.biography}</p>
                      </div>
                      <div className="col-sm-12">
                        <p>Website: {value.website}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <hr />
              {/* {posts} */}
            </React.Fragment>
          );
        })}
      </main>
    );
  }
}

export { Profile };
