/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import React from "react";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = { profile: [] };
  }

  componentDidMount() {
    async function fch() {
      try {
        // console.log(document.cookie);
        // if (document.cookie !== "") {
        //   let user_id_ = document.cookie
        //     .split(";")
        //     .find((e) => e.startsWith("user_id="))
        //     .split("=")[1];
        //   }

        await lg_api("accounts/profile-router/profile-router/").then((res) => {

          console.log(this.state.profile);
        });
      } catch (err) {
        console.log(err);
      }
    }
    fch();
  }

  render() {
    return (
      <main>
        {this.state.profile.map((el, k) => {
          return (
            <main>
              <div key={k} className="container mb-3">
                <div className="row">
                  <div className="col-sm-4 d-flex justify-content-center">
                    <img
                      src={this.state.profile.picture}
                      alt={this.state.profile.user.username}
                      className="rounded-circle picture-profile"
                      width="170px"
                      height="170px"
                    />
                    {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                            /> */}
                  </div>

                  <div className="col-sm-8">
                    <h2 className="username-h2">
                      {/* {this.profile.user.username} */}
                      <span
                        className="ml-5 btn btn-sm btn-outline-info"
                        onClick={() => (window.location.hash = "#edit-profile")}
                      >
                        Edit profile
                      </span>
                      {/*
                                <a
                                href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                </a>
                            */}
                    </h2>

                    <div className="row mt-2 info-profile">
                      <div className="col-sm-4">
                        {this.state.profile.posts_count}
                        <b>785</b> posts
                      </div>
                      <div className="col-sm-4">
                        {this.state.profile.followers}
                        <b>1,401</b> followers
                      </div>
                      <div className="col-sm-4">
                        {this.state.profile.following}
                        <b>491</b> following
                      </div>
                    </div>
                    <div className="row mt-4">
                      <div className="col-sm-12">
                        <p>{this.state.profile.biography}</p>
                      </div>
                      <div className="col-sm-12">
                        <p>Website: {this.state.profile.website}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <hr />
            </main>

            // {/* {posts} */}
          );
        })}
      </main>
    );
  }
}

