// Handle errors

function searchInputs(inputs, key_err) {
  for (let i = 0; i < inputs.length - 1; i++) {
    if (`${inputs[i].id}` === `id_${key_err}`) {
      inputs[i].classList.add("is-invalid");
    }
  }
}

function addInvalidFields(state, targets) {
  Array(state).map((value, i) => {
    if (value.website !== undefined) {
      searchInputs(targets, "website");
    }

    if (value.picture !== undefined) {
      searchInputs(targets, "picture");
    }

    if (value.phone_number !== undefined) {
      searchInputs(targets, "phone_number");
    }

    if (value.date_of_birth !== undefined) {
      searchInputs(targets, "date_of_birth");
    }
  });
}

export { addInvalidFields };
