import "../../css/Main.css";
import React, { useEffect } from "react";
import {
  BsHouse,
  BsBoxArrowRight,
  BsPersonCircle,
  BsChatLeft,
  BsInstagram,
  BsSearch,
  BsPlusCircle,
} from "react-icons/bs";

import { lg_api } from "../../__modules__";
import { showSearchProfiles } from "../users/showSearchProfiles";

function HeadMenu() {
  const [profile, setProfile] = React.useState("");
  const [profiles, getProfiles] = React.useState([]);
  const token = window.localStorage.getItem("token")
  useEffect(() => {
    lg_api("accounts/profile", {
      headers: {
        Authorization: `Token ${token}`,
      },
      params: {
        search_profile: profile,
      },
    })
      .then((res) => {
        getProfiles(res.data.results);
      })
      .catch((err) => {
        console.error(err.response);
      });
  });
  return (
    <div className="App">
      <header className="header">
        <nav className="navbar navbar-expand-lg bg-light">
          <div className="container-fluid">
            <div className="container-logo-link grid">
              <BsInstagram className="icon-gral" />
              <h1 className="fs-title">Lazygram</h1>
            </div>
            <div
              className="collapse navbar-collapse"
              id="navbarSupportedContent"
            >
              <ul className="navbar-nav mx-auto mb-2 mb-lg-0">
                {/* Search */}
                <li className="nav-item grid-search">
                  <span className="nav-link container-icons-link">
                    <BsSearch className="icon-gral" />
                  </span>
                  <input
                    className="form-control"
                    id="search-input"
                    placeholder="Search profile"
                    onChange={(e) => {
                      setProfile(e.target.value);
                    }}
                  />
                  <div>
                    {profiles?.map((value) => {
                      return (
                        <showSearchProfiles
                          picture={value?.picture}
                          username={value?.user?.username}
                        />
                      );
                    })}
                  </div>
                </li>

                {/* MD */}
                <li className="nav-item">
                  <span className="nav-link container-icons-link">
                    <BsChatLeft className="icon-gral" />
                  </span>
                </li>

                {/* Home */}
                <li
                  className="nav-item"
                  onClick={() => (window.location.hash = "")}
                >
                  <span className="nav-link container-icons-link">
                    <BsHouse className="icon-gral" />
                  </span>
                </li>

                {/* User */}
                <li
                  className="nav-item"
                  onClick={() => {
                    window.location.hash = "#profile";
                    window.localStorage.setItem(
                      "profile",
                      window.localStorage.getItem("profile_auth")
                    );
                  }}
                >
                  <span className="nav-link container-icons-link">
                    <BsPersonCircle className="icon-gral" />
                  </span>
                </li>

                {/* New post */}
                <li
                  className="nav-item"
                  onClick={() => (window.location.hash = "#newpost")}
                >
                  <span className="nav-link container-icons-link">
                    <BsPlusCircle className="icon-gral" />
                  </span>
                </li>

                {/* Logout */}
                <li
                  className="nav-item"
                  onClick={() => {
                    window.location.hash = "logout";
                    window.localStorage.removeItem("token");
                    window.localStorage.removeItem("profile_auth");
                    window.localStorage.removeItem("current_profile");
                  }}
                >
                  <span className="nav-link container-icons-link">
                    <BsBoxArrowRight className="icon-gral" />
                  </span>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </header>
    </div>
  );
}

export { HeadMenu };
