import { lg_api } from "../../__modules__";

function likePost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("likes_post")) {

    let url = `posts/${e.nativeEvent.path[4].id}`
    let form_data = new FormData();

    form_data.append('post_image', this.state.image, this.state.image.name);
    form_data.append('likes', this.state.title);
    form_data.append('profile_likedit', this.state.content);
    form_data.append('description', this.state.content);

    lg_api(url,{
      method: "post",
      data: {
        likes: 1,
      },
      headers: {
        'content-type': 'multipart/form-data'
      }
    }).then((res) => {
      console.log(res);
    }).catch((res)=>{
      console.log(res)
    })
  }
}

function commentPost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("comments_post")) {
    console.log("Comment");
  }
}

function searchProfiles(e) {
  console.log(e.target.value);
}

export { likePost, commentPost };
