import React from "react";
import "../../css/Main.css";
import { HeadMenu, ListPosts, Profile, Login } from "../../__modules__";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      hash: '',
    };
  }

  componentDidMount(){

    window.addEventListener('hashchange',()=>{
      this.setState({hash:window.location.hash})
      if(this.state.hash.startsWith('#profile')){
          return(
            <HeadMenu/>
          )
      }
    })
  }
}
export { App };
