/* List posts from lazygram with react */

import { lg_api } from "../../__modules__";
import "../../css/posts/ListPosts.css";
import React from "react";
import {
  BsFillHeartFill,
  BsFillChatFill,
  BsFillBookmarkFill,
  BsShareFill,
} from "react-icons/bs";

import {
  likePost,
  inputComment,
  allCommentsPost,
} from "../../utils/posts/posts";

class ListPosts extends React.Component {
  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { posts: [], comments: [] };
    this.token = window.localStorage.getItem("token");
    this.http_media = "http://localhost:8000/media/";
    this.post_img = "";
    this.created = "";
    this.handledPosts = (e) => {
      likePost(e);
    };
    this.getLikes = this.getLikes.bind(this);
    this.addHTTPMedia = this.addHTTPMedia.bind(this);
    this.goToProfile = this.goToProfile.bind(this);
    this.commentComponent = this.commentComponent.bind(this);
  }

  commentComponent(e) {
    let len = e.target.id.split("").length;
    let id = e.target.id.slice(11, len);
    lg_api(`comments/`, {
      headers: {
        Authorization: `Token ${this.token}`,
      },
      params: { id_post: id },
    })
      .then((res) => {
        this.setState({ comments: res.data });
      })
      .catch((err) => {
        console.error(err.response);
      });
  }

  /**
   * If a profile picture not find,
   * return a profile picture by default.
   * @param {*} post
   */
  goToProfile(e) {
    console.log(e.nativeEvent.path[2].childNodes[1].innerText);
    let profile =
      e.nativeEvent.path[2].childNodes[1].innerText || e.target.innerText;
    window.localStorage.setItem("profile", `${profile}`);
    window.location.hash = "#profile";
  }

  addHTTPMedia(post) {
    // If post image not contains http_media, add.
    if (!post.picture.startsWith(this.http_media)) {
      this.post_img = this.http_media + post.picture;
    } else {
      this.post_img = post.picture;
    }
  }

  async getLikes(e) {
    await lg_api(
      `posts/${String(e.nativeEvent.path[1].id).startsWith("likes_post")}`,
      {
        headers: {
          Authorization: `Token ${this.token}`,
        },
      }
    ).then((res) => {
      console.log(e);
    });
  }

  componentDidMount() {
    // Getting posts.
    lg_api("posts/", {
      headers: {
        Authorization: `Token ${this.token}`,
      },
    })
      .then((res) => {
        this.setState({ posts: res.data });
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  }

  render() {
    return (
      <section className="">
        {this.state.posts.map((post, i) => {
          this.addHTTPMedia(post);

          return (
            <div
              key={i}
              id={post.id}
              className="row w-50 mx-auto mb-4 p-2 posts border border-4"
              onClick={(e) => this.handledPosts(e)}
            >
              {/* User profile image */}
              <div className="row align-items-center">
                <div className="col-sm-1">
                  <img
                    className="profile-img-post rounded-circle go-to-profile"
                    src={post.profile?.picture}
                    alt={post.profile?.picture}
                    onClick={(e) => this.goToProfile(e)}
                  />
                </div>

                {/* User profile name */}
                <div className="col-sm">
                  <h5
                    className="h5 go-to-profile"
                    onClick={(e) => this.goToProfile(e)}
                  >
                    {post.profile?.user?.username}
                  </h5>
                </div>

                {/* Created */}
                <div className="">
                  <p className="h6 pt-1 px-1 text-black-50">Created: {post.created}</p>
                </div>
              </div>

              {/* Post img */}
              <div className="">
                <img
                  className="w-100 rounded-top img-post"
                  src={this.post_img}
                  alt={this.post_img}
                />

                {/* Like | comment | share | save post */}
                <div className="feedback">
                  {/* Like */}
                  <BsFillHeartFill
                    className="icon-size-post"
                    id={`id_like_post_${post.id}`}
                  />

                  {/* Comment */}
                  <BsFillChatFill
                    className="icon-size-post"
                    onClick={(e) => {
                      let a = inputComment(e.target.parentElement.id);
                      e.nativeEvent.path[4].appendChild(a);
                    }}
                    id={`id_comments_post_${post.id}`}
                  />

                  {/* Share */}
                  <BsShareFill
                    className="icon-size-post"
                    id={`id_share_post_${post.id}`}
                  />

                  {/* Save post */}
                  <BsFillBookmarkFill
                    className="icon-size-post"
                    id={`id_save_post_${post.id}`}
                  />
                </div>

                {/* Likes count */}
                <div>
                  <span
                    className="text-end fs-6 likes"
                    id={`id_show_likes_${post.id}`}
                  >
                    {post?.likes} Likes
                  </span>
                </div>

                {/* Description */}
                <div className="mt-2">
                  <p className="fs-6">
                    <span className="h6 me-2">{post.description}</span>
                  </p>
                </div>

                {/* Comments */}
                <div>
                  <button
                    className="btn btn-outline-info"
                    id={`id_btn_cmt_${post.id}`}
                    onClick={(e) => {
                      this.commentComponent(e);
                    }}
                  >
                    Show comments...
                  </button>
                  <div className="" id={`id_show_comments_${post.id}`}>
                    {this.state?.comments.map((value) => {
                      if (value?.created) {
                        this.created = value?.created.slice(0, 10);
                      }

                      return (
                        <div
                          className="row-profile-pic align-items-center mt-2 p-2 rounded border border-1"
                          key={value?.id}
                        >
                          <div>
                            <img
                              className="profile-img-comment rounded-circle go-to-profile"
                              src={value?.commented_by?.picture}
                              alt={value?.commented_by?.user?.username}
                            />
                          </div>

                          <div>
                            <h6 className="h5">
                              {value?.commented_by?.user?.username} :
                            </h6>
                          </div>
                          <div>
                            <p className="fs-5">{value?.comment}</p>
                            <div>
                              <span className="fs-comment text-black-50">{this.created}</span>
                            </div>
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </section>
    );
  }
}

export { ListPosts };
