/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import React, { useEffect, useState } from "react";

class Profile extends React.Component {
  constructor(props) {
    this.state = { profile: [] };
  }

  componentDidMount() {
    try {
      if (document.cookie !== "") {
        let user_id_ = document.cookie
          .split(";")
          .find((e) => e.startsWith("user_id="))
          .split("=")[1];

        lg_api("accounts/profile-router/profile-router/?user_id=1", {
          params: {
            user_id: user_id_,
          },
        }).then((res) => {
          this.setProfile(res.data);
        });
      }
    } catch (err) {
      console.log(err);
    }
  }
}

