/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Login.css";
import React from "react";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = { log: [] };
    this.username = props.username;
    this.password = props.password;
    this.img = "http://localhost:8000/media/logo_instagram.png";
    this.login_submit = (e) => {

      e.stopPropagation()
      console.log(e)
      lg_api({
        method:'post',
        url:'accounts/login',
        data:{
          username:this.username,
          password:this.password,
        }
      })
        .then((res) => {
          this.setState({ log: res.data });
        });
    };
  }

  render() {
    return (
      <main>
        <section className="login">
          <div className="logo">
            <img src={this.img} alt="copygram_logo" />
            <h1>Lazygram</h1>
          </div>
          <div className="container-form">
            <form method="POST">
              <div className="container-form">
                <label htmlFor="id_username">Username</label>
                <input
                  className="form-control"
                  id="id_username"
                  placeholder="Username"
                  autoFocus
                  onChange={(e)=>{this.username=e}}
                ></input>

                <label htmlFor="id_password">Password</label>
                <input
                  className="form-control"
                  id="id_password"
                  placeholder="Password"
                  min={8}
                  type="password"
                ></input>
                <div className="d-grid gap-2 col-12 my-2">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    onSubmit={(e) => {this.login_submit(e)}}
                  >
                    Login
                  </button>
                </div>
                <div className="mx-auto">
                  <span className="dont-account" onClick={() => {window.location.hash = "#register"}}>
                    Don't have an account? register now!
                  </span>
                </div>
              </div>
            </form>
          </div>
        </section>
      </main>
    );
  }
}

export { Login };
