import React from "react";
import { lg_api } from "../../__modules__";

class CreatePost extends React.Component {
  constructor() {
    super();
    this.state = { post: [] };
  }

  componentDidMount() {
    let form_post = new FormData();
		let picture_post = document.querySelector("#id_picture_post").value

    lg_api("posts/", {
      method: "post",
      headers: {
        Authorization: `Token ${this.token}`,
      },
      data: form_post,
    });
  }
}

export { CreatePost };
