/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Profile.css";
import React from "react";

class Profile extends React.Component {
  constructor(props) {
    super(props);
    this.state = { profile: [] };
  }

  componentDidMount() {
    try {
      console.log(document.cookie);
      let auth_user = window.localStorage.getItem("user");
      if (auth_user !== null) {
        let user_id_ = document.cookie
          .split(";")
          .find((e) => e.startsWith("user="))
          .split("=")[1];

        lg_api("accounts/profile/root", {
          params: {
            user: user_id_,
          },
        }).then((res) => {
          this.setState({ profile: res.data });
          console.log(this.state.profile);
        });
      }
    } catch (err) {
      console.log(err);
    }
  }

  render() {
    return (
      <main>
        {this.state.profile.map((elem, k) => {
          return (
            <React.Fragment key={k}>
              <div className="container mb-3">
                <div className="row">
                  <div className="col-sm-4 d-flex justify-content-center">
                    <img
                      src={elem.picture}
                      alt={elem.user.username}
                      className="rounded-circle picture-profile"
                      width="170px"
                      height="170px"
                    />
                    {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                            /> */}
                  </div>

                  <div className="col-sm-8">
                    <h2 className="username-h2">
                      {elem.user.username}
                      <span
                        className="ml-5 btn btn-sm btn-outline-info"
                        onClick={() => (window.location.hash = "#edit-profile")}
                      >
                        Edit profile
                      </span>
                      {/*
                                <a
                                href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                </a>
                            */}
                    </h2>

                    <div className="row mt-2 info-profile">
                      <div className="col-sm-4">
                        <b>{elem.posts_count}</b> posts
                      </div>
                      <div className="col-sm-4">
                        <b>{elem.followers}</b> followers
                      </div>
                      <div className="col-sm-4">
                        <b>{elem.following}</b> following
                      </div>
                    </div>
                    <div className="row mt-4">
                      <div className="col-sm-12">
                        <p>{elem.biography}</p>
                      </div>
                      <div className="col-sm-12">
                        <p>Website: {elem.website}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <hr />
              {/* {posts} */}
            </React.Fragment>
          );
        })}
      </main>
    );
  }
}

