/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Login.css";
import "../../../../imgs"
import React from "react";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = { log: [] };
    this.username = props.username;
    this.password = props.password;
  }

  componentDidMount() {
    lg_api
      .post("accounts/login/", {
        username: "root",
        password: "admin123",
      })
      .then((res) => {
        this.setState({ log: res.data });
      });
  }

  render() {
    return (
      <main>
        <section className="login">
          <div className="logo">
            <img src="" alt="copygram_logo" />
            <h1>Lazygram</h1>
          </div>
          <div className="container-form">
            <form method="POST">
              <div className="container-form">
                <label for="id_username">Username</label>
                <input id="id_username"></input>

                <label for="id_password">Password</label>
                <input id="id_password"></input>
                <div className="d-grid gap-2 col-12 my-2">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    onSubmit={(e) => {}}
                  >
                    Login
                  </button>
                </div>
                <div className="mx-auto">
                  <span>Don't have an account? register now!</span>
                </div>
              </div>
            </form>
          </div>
        </section>
      </main>
    );
  }
}

export { Login };
