/* List posts from lazygram with react */

import { lg_api } from "../__modules__";
import "../../css/posts/ListPosts.css";
import React, { useState, useEffect } from "react";
import {
  BsFillHeartFill,
  BsFillChatFill,
  BsFillBookmarkFill,
  BsShareFill,
} from "react-icons/bs";

function ListPosts() {
  /* Return posts that are getting from backend */
  const [posts, setPosts] = useState([]);
  useEffect(() => {
    async function fetchData() {
      try {
        const res = await lg_api("posts/");
        setPosts(res.data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchData();
  }, []);

  let http_media = "http://localhost:8000/media/"
  let post_img;

  return (
    <section className="">
      {posts.map((post, i) => {
        console.log(post);
        if(!String(post.post_image).startsWith(http_media)){

        }
        return (
          <div key={i} className="row w-50 mx-auto mb-4">
            {/* User profile image */}
            <div className="row align-items-center">
              <div className="col-sm-1">
                <img
                  className="profile-img-post rounded-circle"
                  src={post.profile_name.picture}
                  alt=""
                />
              </div>
              {/* User profile name */}
              <div className="col-sm">
                <h5 className="h5">{post.profile_name.user.username}</h5>
              </div>
              {/* Created */}
              <div className="">
                <p className="h6 pt-1 px-1">Created: {post.created}</p>
              </div>
            </div>
            {/* Post img */}
            <div className="">
              <img
                className="w-100 rounded-top img-post"
                src={a+post.post_image}
                alt={a+post.post_image}
              />
              {/* Like | comment | share | save post */}
              <div className="feedback">
                {/* Like */}
                <BsFillHeartFill className="icon-size-post" />
                {/* Comment */}
                <BsFillChatFill className="icon-size-post" />
                {/* Share */}
                <BsShareFill className="icon-size-post" />
                {/* Save post */}
                <BsFillBookmarkFill className="icon-size-post" />
              </div>
              {/* Likes count */}
              <div>
                <span className="text-end fs-6"> 300,000 likes</span>
              </div>
              {/* Description */}
              <div className="mt-2">
                <p className="fs-6">
                  <span className="h6 me-2">{post.description}</span>
                </p>
              </div>
            </div>
          </div>
        );
      })}
    </section>
  );
}

export { ListPosts };
