/* List posts from lazygram with react */

import { lg_api } from "../__modules__";
import "../../css/posts/ListPosts.css";
import React from "react";
import {
  BsFillHeartFill,
  BsFillChatFill,
  BsFillBookmarkFill,
  BsShareFill,
} from "react-icons/bs";

function ListPosts() {
  /* Return posts that are getting from backend */

  async function fetched() {
    const { data } = lg_api("posts/");
    return data;
  }

  return fetched().then((res) => {

    <section className="list-section">
      <div className="row w-50 mx-auto mb-4">
        {/* User profile image */}
        <div className="row align-items-center">
          <div className="col-sm-1">
            <img className="profile-img-post rounded-circle" src="" alt="" />
          </div>
          {/* User profile name */}
          <div className="col-sm">
            <h5 className="h5">.</h5>
          </div>
          <div className="">
            <p className="h6 pt-1 px-1"></p>
          </div>
        </div>
        {/* Post img */}
        <div className="">
          <img className="w-100 rounded-top img-post" src="" alt="" />
          {/* Like | comment | share | save post */}
          <div className="feedback">
            {/* Like */}
            <BsFillHeartFill className="icon-size-post" />
            {/* Comment */}
            <BsFillChatFill className="icon-size-post" />
            {/* Share */}
            <BsShareFill className="icon-size-post" />
            {/* Save post */}
            <BsFillBookmarkFill className="icon-size-post" />
          </div>
          {/* Likes count */}
          <div>
            <span className="text-end fs-6"> 300,000 likes</span>
          </div>
          {/* Description */}
          <div className="mt-2">
            <p className="fs-6">
              <span className="h6 me-2">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Odit
                deleniti accusantium et vitae ipsam mollitia molestiae
                voluptatem pariatur nihil. Similique tenetur commodi repellat
                sint expedita impedit aut obcaecati deleniti esse.
              </span>
            </p>
          </div>
        </div>
      </div>
    </section>;
  });
}

export { ListPosts };
