/* List posts from lazygram with react */

import { lg_api } from "../../__modules__";
import "../../css/posts/ListPosts.css";
import React, { useState, useEffect } from "react";
import {
  BsFillHeartFill,
  BsFillChatFill,
  BsFillBookmarkFill,
  BsShareFill,
} from "react-icons/bs";

import { likePost, commentPost } from "../../utils/posts/posts";

class ListPosts extends React.Component {
  componentDidMount() {
    try {
      lg_api("posts/").then((res) => {
        this.setState(res.data);
      });
    } catch (err) {
      console.log(err);
    }
  }

  constructor(props) {
    super(props);
    // Don't call this.setState() here!
    this.state = { posts: [] };
    this.likesid = props.likesid;
    this.commentsid = props.commentsid;
    this.savedid = props.savedid;
    this.http_media = "http://localhost:8000/media/";
    function handledPosts(e) {
      likePost(e);
      commentPost(e);
    }
  }
  post_img;

  render() {
    return (
      <section className="">
        {this.posts.map((post, i) => {
          // If post image not contains http_media, add.
          if (!post.post_image.startsWith(this.http_media)) {
            this.post_img = this.http_media + post.post_image;
          } else {
            this.post_img = post.post_image;
          }

          // If doesn't likes
          if (post.likes == null) {
            post.likes = 0;
          }

          // Add id
          this.likesid = "likes_post_" + post.id;
          this.commentsid = "comments_post_" + post.id;
          this.shareid = "share_post_" + post.id;
          this.savedid = "save_post_" + post.id;

          return (
            <div
              key={i}
              id={post.id}
              className="row w-50 mx-auto mb-4 posts"
              onClick={(e) => this.handledPosts(e)}
            >
              {/* User profile image */}
              <div className="row align-items-center">
                <div className="col-sm-1">
                  <img
                    className="profile-img-post rounded-circle"
                    src={post.profile_name.picture}
                    alt={post.profile_name.picture}
                  />
                </div>
                {/* User profile name */}
                <div className="col-sm">
                  <h5 className="h5">{post.profile_name.user.username}</h5>
                </div>
                {/* Created */}
                <div className="">
                  <p className="h6 pt-1 px-1">Created: {post.created}</p>
                </div>
              </div>
              {/* Post img */}
              <div className="">
                <img
                  className="w-100 rounded-top img-post"
                  src={this.post_img}
                  alt={this.post_img}
                />
                {/* Like | comment | share | save post */}
                <div className="feedback">
                  {/* Like */}
                  <BsFillHeartFill
                    className="icon-size-post"
                    id={this.likesid}
                  />
                  {/* Comment */}
                  <BsFillChatFill
                    className="icon-size-post"
                    id={this.commentsid}
                  />
                  {/* Share */}
                  <BsShareFill className="icon-size-post" id={this.shareid} />
                  {/* Save post */}
                  <BsFillBookmarkFill
                    className="icon-size-post"
                    id={this.savedid}
                  />
                </div>
                {/* Likes count */}
                <div>
                  <span className="text-end fs-6 likes">
                    {post.likes} likes
                  </span>
                </div>
                {/* Description */}
                <div className="mt-2">
                  <p className="fs-6">
                    <span className="h6 me-2">{post.description}</span>
                  </p>
                </div>
              </div>
            </div>
          );
        })}
      </section>
    );
  }
}

export { ListPosts };
