import { lg_api } from "../../__modules__";

async function likePost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("likes_post")) {
    let url = `posts/${e.nativeEvent.path[4].id}`;

    // Update likes from post.
    const {data} = await lg_api("accounts/profile-router/profile-router/",{
      params: { user_id: document.cookie.user_id}
    });

    await lg_api(url).then((res) => {
      lg_api(url, {
        method: "post",
        data: {
          post_image: res.post_image,
          likes: 1,
          description: res.description,
        },
        headers: {
          "content-type": "multipart/form-data",
          "Access-Control-Allow-Origin": "http://localhost:8000/",
          "Access-Control-Allow-Headers": "*",
        },
      })
        .then((res) => {
          console.log(res);
        })
        .catch((res) => {
          console.log(res);
        });
    });
  }
}

function commentPost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("comments_post")) {
    console.log("Comment");
  }
}

function searchProfiles(e) {
  console.log(e.target.value);
}

export { likePost, commentPost };
