import { lg_api } from "../../__modules__";

async function likePost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("likes_post")) {
    let url = `posts/${e.nativeEvent.path[4].id}/`;
    let token = window.localStorage.getItem("token");
    let profile_likedit = window.localStorage.getItem("profile_auth");

    await lg_api(url, {
      method: "put",
      headers: {
        Authorization: `Token ${token}`,
      },
      data: {
        profile_likedit: profile_likedit,
      },
    })
      .then((res) => {
        e.target.innerHTML = res.data.likes;
        let likesComponent = document.querySelector("#id_likes");
        likesComponent

        console.log(e);
      })
      .catch((err) => {
        console.log(err.response.data);
      });
  }
}

async function commentPost(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("comments_post")) {
    try {
      let form_comment = new FormData();
      let postid = parseInt(e.nativeEvent.path[4].id);
      let token = window.localStorage.getItem("token");
      let comment_by = window.localStorage.getItem("profile_auth");
      let comment = e.target.value;

      if (postid !== null) {
        form_comment.append("post", postid);
      }
      if (comment)
        if (comment_by !== null) {
          form_comment.append("comment_by", comment_by);
        }
      if (comment !== null) {
        form_comment.append("comment", comment);
      }

      // Put comments.
      lg_api(`comments/`, {
        method: "post",
        headers: {
          Authorization: `Token ${token}`,
        },
        data: form_comment,
      })
        .then((res) => {
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err.response.data);
        });
    } catch (err) {
      alert(err.name, err.message);
      console.log(err.message);
    }
  }
}

async function replyComment(e) {
  if (String(e.nativeEvent.path[1].id).startsWith("comments_post")) {
    try {
      let token = window.localStorage.getItem("token");
      let replied_by = window.localStorage.getItem("profile_auth");
      let form_comment = new FormData();
      let len = e.target.parentElement.id.split("").length;
      let commentid = parseInt(e.target.parentElement.id.split("")[len - 1]);
      console.log("Comment");

      let reply = e.target.value;

      // Comment id validation.
      if (commentid !== null) {
        form_comment.append("commentid", commentid);
      }

      // Replied by validation.
      if (replied_by !== null) {
        form_comment.append("replied_by", replied_by);
      }

      // Reply validation.
      if (reply !== null) {
        form_comment.append("reply", reply);
      }

      // Put comments.
      lg_api(`replies/`, {
        method: "put",
        headers: {
          Authorization: `Token ${token}`,
        },
        data: form_comment,
      })
        .then((res) => {
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err.response.data);
        });
    } catch (err) {
      alert(err.name, err.message);
      console.log(err.message);
    }
  }
}

