/* Login view from lazygram in react */

import { lg_api } from "../../__modules__";
import "../../css/users/Profile.css";
import React from "react";

class Profile extends React.Component {
  constructor() {
    super();
    this.state = { profile: [], followers: [], following: [] };
  }

  componentDidMount() {
    try {
      let auth_user = window.localStorage.getItem("user");
      if (auth_user !== null) {
        lg_api(`accounts/profile/${auth_user}`, {
          headers: `Authorization: Token ${window.localStorage.getItem(
            "token"
          )}`,
        }).then((res) => {
          this.setState({ profile: [res.data] });
          console.log(res.data);
        });

        lg_api(`accounts/followers/${auth_user}`, {
          headers: `Authorization: Token ${window.localStorage.getItem(
            "token"
          )}`,
        }).then((res) => {
          this.setState({ followers: [res.data] });
        });

        lg_api(`accounts/following/${auth_user}`).then((res) => {
          this.setState({ following: [res.data] });
          console.log(this.state.followers);
        });
      }
    } catch (err) {
      console.log(err);
      this.setState({ profile: [err] });
    }
  }

  render() {
    return (
      <main>
        {this.state.profile.map((value, i) => {
          if (value.picture === undefined) {
            value.picture = "http://localhost:8000/media/user_circle.svg";
          }
          return (
            <React.Fragment key={i}>
              <div className="container mb-3">
                <div className="row">
                  <div className="col-sm-4 d-flex justify-content-center">
                    <img
                      src={value.picture}
                      alt={value.user.username}
                      className="rounded-circle picture-profile"
                      width="170px"
                      height="170px"
                    />
                    {/* <img
                            src="{% static 'imgs/sin_perfil.png' %}"
                            alt="@{{ profile.user}}"
                            class="rounded-circle"
                            width="170px"
                            height="170px"
                            style="object-fit: cover;"
                          /> */}
                  </div>

                  <div className="col-sm-8">
                    <h2 className="username-h2">
                      {value.user.username}
                      <span
                        className="ml-5 btn btn-sm btn-outline-info"
                        onClick={() => (window.location.hash = "#edit-profile")}
                      >
                        Edit profile
                      </span>
                      {/*
                                <a
                                href=""
                                    class="ml-5 btn btn-sm btn-primary"
                                >
                                    Follow
                                    </a>
                            */}
                    </h2>

                    <div className="row mt-2 info-profile">
                      <div className="col-sm-4">
                        <b>{value.posts_count}</b> posts
                      </div>
                      <div className="col-sm-4">
                        <b>{this.state.followers[0]?.followers_length}</b>{" "}
                        followers
                      </div>
                      <div className="col-sm-4">
                        <b>{this.state.following[0]?.following_length}</b>{" "}
                        following
                      </div>
                    </div>
                    <div className="row mt-4">
                      <div className="col-sm-12">
                        <p>{value.biography}</p>
                      </div>
                      <div className="col-sm-12">
                        <p>Website: {value.website}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <hr />
              {/* {posts} */}
            </React.Fragment>
          );
        })}
      </main>
    );
  }
}

export { Profile };
