/* Edit profile. */

import { lg_api } from "../../__modules__";
import "../../css/users/Profile.css";
import React from "react";

class EditProfile extends React.Component {
  constructor() {
    super();
    this.state = { profile_data: [] };
    this.biography = ''
  }

  componentDidMount() {
    let auth_user = window.localStorage.getItem("user");
    lg_api(`accounts/profile/${auth_user}`).then((res) => {
      this.setState({ profile_data: res.data });
    });
  }

  render() {
    let user = this.state.profile_data;
    console.log(user);
    return (
      <form method="post" encType="multipart/form-data">
        <div className="mx-auto w-75">
          {/* Title edit profile */}
          <div className="mb-2 col-sm-8 mx-auto">
            <h2 className="h2">Edit user profile</h2>
          </div>

          {/* Biography */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_biography" className="form-label">
              Biography:
            </label>
            <input
              type="text"
              placeholder="Your biography"
              id="id_biography"
              value={user?.biography || this.biography}
              className="form-control"
              onChange={(e) => {
                this.handleInput(e);
              }}
            />
          </div>

          {/* Email */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_email" className="form-label">
              Email:
            </label>
            <input
              type="text"
              className="form-control"
              pattern=".+@gmail\.com"
              maxLength="170"
              minLength="8"
              id="id_email"
              placeholder="youremail@domain.com"
              aria-label="Your email"
            />
          </div>

          {/* Picture */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_picture" className="form-label">
              Picture:
            </label>
            <input
              type="file"
              id="id_picture"
              accept="image/png, image/jpeg"
              className="form-control"
            />
          </div>

          {/* Date of birth */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_date_of_birth" className="form-label">
              Date of birth:
            </label>
            <input type="date" id="id_date_of_birth" className="form-control" />
          </div>

          {/* Website */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_website" className="form-label">
              Website:
            </label>
            <input
              type="text"
              placeholder="https://website.com"
              id="id_website"
              pattern="https://.*"
              maxLength="200"
              className="form-control"
            />
          </div>

          {/* Phone number */}
          <div className="mb-2 col-sm-8 mx-auto">
            <label htmlFor="id_phone_number" className="form-label">
              Phone number:
            </label>
            <input
              type="number"
              id="id_phone_number"
              placeholder="Phone number"
              max="17"
              min="0"
              className="form-control"
            />
          </div>

          {/* Update profile */}
          <div className="d-grid gap-2 col-sm-8 mx-auto mb-2">
            <button type="submit" className="btn btn-primary">
              Update
            </button>
          </div>
        </div>
      </form>
    );
  }
}

export { EditProfile };
